import React, { useState } from 'react'
import { Link, Redirect } from 'react-router-dom/cjs/react-router-dom.min';



export default function () {

    const LOCAL_STORAGE_EMPARR = 'emparr';
    //emparr is functional object
    const [emparr, setEmparr] = useState([]);
    const [firstTimeLoad, setFirstTimeLoad] = useState(true);
    //we cannot add an employee to this array directly so we will have to use another array
    const addEmployee = (ob) => {
        //[...emparr,{...ob}] is spread operator which internally creates a copy of emp array and add the properties of ob object int that array
        let newemparr = [...emparr, { ...ob }];
        //now we use setEmparr to set emparr as newemparr.
        setEmparr(newemparr);
    }
    //useEffect hook is used to call an fucntion whenever the value of an state variable changes
    //it takes two arguments useEffect(()=>{function},[dependency])
    //the function will be called whenever the state of the dependency changes
    useEffect(() => {
        const arr = JSON.parse(localStorage.getItem(LOCAL_STORAGE_EMPARR));
        if (arr) {
            setEmparr(arr);
        }
    }, [])

    useEffect(() => {
        if (firstTimeLoad) {
            setFirstTimeLoad(false);
        }
        else {
            localStorage.setItem(LOCAL_STORAGE_EMPARR, JSON.stringify(emparr));
        }
    }, [emparr]);
    //STOP
    function validateCredentials(event) {
        event.preventDefault();
        if (password === confirmPassword && aadhar === 12345678 && password == "gaurav")
            //return window.location.href = '/Home';
            return (<Redirect to="/Home" />);
    };

    function onAadharChange(event) {
        setAadhar(event.target.value);
    };
    function onPasswordChange(event) {
        setPassword(event.target.value);
    }
    function onConfirmPasswordChange(event) {
        setConfirmPassword(event.target.value);
    }
    const [aadhar, setAadhar] = useState();
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    return (
        <div>
            <div className="container border rounded w-50 p-3 mt-5">
                <form>
                    <h1>New Citizen Registration</h1>
                    <div className="form-group">
                        <label for="aadhar_id">Enter AadharID</label>
                        <input type="number" className="form-control" id="aadhar_id" placeholder="Enter AadharID" required value={aadhar} onChange={onAadharChange} />
                    </div>
                    <div className="form-group">
                        <label for="password">Password</label>
                        <input type="password" className="form-control" id="password" placeholder="Enter Your Password" required value={password} onChange={onPasswordChange} />
                    </div>
                    <div className="form-group">
                        <label for="password_again">Confirm Password</label>
                        <input type="password" className="form-control" id="password_again" placeholder="Confirm Your Password" required value={confirmPassword} onChange={onConfirmPasswordChange} />
                    </div>
                    <button type="submit" className="btn btn-primary mt-2 me-2" onClick={validateCredentials}>Submit</button>
                    <button type="reset" className="btn btn-primary mt-2">Reset</button>
                </form>
            </div>
        </div>
    )
}
